enum class OrderStatus(val label: String) {
      Cancelled("CANCELLED"),
      Confirmed("CONFIRMED"),
      Delivered("DELIVERED"),
      OutForDelivery("OUT_FOR_DELIVERY"),
      Pending("PENDING"),
      Preparing("PREPARING"),
      ReadyForDelivery("READY_FOR_DELIVERY"),
      Refunded("REFUNDED");
        
  companion object {
    @JvmStatic
    fun valueOfLabel(label: String): OrderStatus? {
      return values().find { it.label == label }
    }
  }
}



