changeSet.sql:
CREATE OR REPLACE FUNCTION update_file_metadata_updated_at()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END
$$ LANGUAGE plpgsql;

CREATE TRIGGER trigger_file_metadata_updated_at
BEFORE UPDATE ON file_metadata
FOR EACH ROW
EXECUTE FUNCTION update_file_metadata_updated_at();

ALTER TABLE reconciliation_tasks
ADD CONSTRAINT chk_task_type
CHECK (task_type IN ('PAYMENT', 'REFUND', 'SETTLEMENT'));

ALTER TABLE reconciliation_tasks
ADD CONSTRAINT chk_task_status
CHECK (status IN ('PENDING', 'RUNNING', 'SUCCESS', 'FAILED'));

ALTER TABLE reconciliation_tasks
ADD CONSTRAINT chk_total_records_non_negative
CHECK (total_records >= 0);

ALTER TABLE reconciliation_tasks
ADD CONSTRAINT chk_matched_records_non_negative
CHECK (matched_records >= 0);

ALTER TABLE reconciliation_tasks
ADD CONSTRAINT chk_unmatched_records_non_negative
CHECK (unmatched_records >= 0);

ALTER TABLE message_history
ADD CONSTRAINT chk_message_status
CHECK (status IN ('PENDING', 'SENT', 'FAILED', 'RETRYING'));

ALTER TABLE message_history
ADD CONSTRAINT chk_message_status
CHECK (status IN ('PENDING', 'SENT', 'FAILED', 'RETRYING'));

CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END
$$ language 'plpgsql';

CREATE TRIGGER update_user_notification_settings_updated_at
BEFORE UPDATE ON user_notification_settings
FOR EACH ROW EXECUTE FUNCTION update_updated_at_column();

ALTER TABLE delivery_worker_statistics
ADD CONSTRAINT chk_average_rating_range
CHECK (average_rating >= 0.00 AND average_rating <= 5.00);

ALTER TABLE delivery_worker_statistics
ADD CONSTRAINT chk_total_reviews_non_negative
CHECK (total_reviews >= 0);

ALTER TABLE delivery_worker_statistics
ADD CONSTRAINT chk_star_reviews_non_negative
CHECK (one_star_reviews >= 0 AND two_star_reviews >= 0 AND three_star_reviews >= 0 AND four_star_reviews >= 0 AND five_star_reviews >= 0);

CREATE OR REPLACE FUNCTION update_delivery_worker_statistics_last_updated()
RETURNS TRIGGER AS $$
BEGIN
    NEW.last_updated = CURRENT_TIMESTAMP;
    RETURN NEW;
END
$$ language 'plpgsql';

CREATE TRIGGER trigger_delivery_worker_statistics_last_updated
BEFORE UPDATE ON delivery_worker_statistics
FOR EACH ROW
EXECUTE FUNCTION update_delivery_worker_statistics_last_updated();

ALTER TABLE reconciliation_discrepancies
ADD CONSTRAINT chk_discrepancy_type
CHECK (discrepancy_type IN ('MISSING', 'MISMATCH', 'EXTRA'));

ALTER TABLE reconciliation_discrepancies
ADD CONSTRAINT chk_source_system
CHECK (source_system IN ('INTERNAL', 'WECHAT'));

ALTER TABLE reconciliation_discrepancies
ADD CONSTRAINT chk_discrepancy_status
CHECK (status IN ('UNRESOLVED', 'RESOLVED'));

ALTER TABLE order_items ADD CONSTRAINT chk_order_items_quantity_positive
CHECK (quantity > 0);

ALTER TABLE order_items ADD CONSTRAINT chk_order_items_unit_price_positive
CHECK (unit_price > 0);

ALTER TABLE order_items ADD CONSTRAINT chk_order_items_total_price_positive
CHECK (total_price > 0);

ALTER TABLE payments ADD CONSTRAINT chk_payments_amount_positive
CHECK (amount > 0);

ALTER TABLE payments ADD CONSTRAINT chk_payments_currency_code
CHECK (LENGTH(currency) = 3);

ALTER TABLE payments ADD CONSTRAINT chk_payments_status
CHECK (status IN ('PENDING', 'PAID', 'FAILED', 'CANCELLED', 'REFUNDED'));

ALTER TABLE payment_refunds ADD CONSTRAINT chk_payment_refunds_amount_positive
CHECK (amount > 0);

ALTER TABLE payment_refunds ADD CONSTRAINT chk_payment_refunds_status
CHECK (status IN ('PENDING', 'SUCCESS', 'FAILED', 'CANCELLED'));

ALTER TABLE reviews
ADD CONSTRAINT chk_reviews_rating_range
CHECK (rating >= 1 AND rating <= 5);

ALTER TABLE reviews
ADD CONSTRAINT chk_reviews_comment_length
CHECK (LENGTH(COALESCE(comment, '')) <= 500);

CREATE OR REPLACE FUNCTION update_reviews_updated_at()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END
$$ language 'plpgsql';

CREATE TRIGGER trigger_reviews_updated_at
BEFORE UPDATE ON reviews
FOR EACH ROW
EXECUTE FUNCTION update_reviews_updated_at();

CREATE OR REPLACE FUNCTION update_updated_at_column()
RETURNS TRIGGER AS $$
BEGIN
    NEW.updated_at = CURRENT_TIMESTAMP;
    RETURN NEW;
END
$$ LANGUAGE plpgsql;

DROP TRIGGER IF EXISTS update_reconciliation_tasks_updated_at ON reconciliation_tasks;
CREATE TRIGGER update_reconciliation_tasks_updated_at
    BEFORE UPDATE ON reconciliation_tasks
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();

DROP TRIGGER IF EXISTS update_reconciliation_discrepancies_updated_at ON reconciliation_discrepancies;
CREATE TRIGGER update_reconciliation_discrepancies_updated_at
    BEFORE UPDATE ON reconciliation_discrepancies
    FOR EACH ROW
    EXECUTE FUNCTION update_updated_at_column();
